package net.civeira.phylax.features.access.user.transition.changepassword;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.user.User;
import net.civeira.phylax.features.access.user.transition.UserChangeProposal;

@ApplicationScoped
@RequiredArgsConstructor
public class UserChangePasswordPipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<UserChangePasswordStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param password
   * @param original
   * @param last
   * @return
   */
  public User changePassword(final String password, final User original,
      final Function<UserChangeProposal, User> last) {
    Function<UserChangePasswordTransition, User> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.apply(t.getData());
    };
    List<UserChangePasswordStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<UserChangePasswordTransition, User> pipeline = reversed.stream().reduce(finalHandler,
        (next, step) -> transition -> step.changePassword(transition, next), (f1, f2) -> f1);
    return pipeline.apply(UserChangePasswordTransition.builder()
        .data(new UserChangeProposal(original)).original(original).build());
  }
}
