package net.civeira.phylax.features.access.user.transition.delete;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;
import java.util.function.Supplier;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.user.User;

@ApplicationScoped
@RequiredArgsConstructor
public class UserDeletePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<UserDeleteStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public User delete(final User original, final Supplier<User> last) {
    Function<UserDeleteTransition, User> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.get();
    };
    List<UserDeleteStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<UserDeleteTransition, User> pipeline = reversed.stream().reduce(finalHandler,
        (next, step) -> transition -> step.delete(transition, next), (f1, f2) -> f1);
    return pipeline.apply(UserDeleteTransition.builder().original(original).build());
  }
}
