package net.civeira.phylax.features.access.user.transition.setmfaseed;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.user.User;
import net.civeira.phylax.features.access.user.transition.UserChangeProposal;

@ApplicationScoped
@RequiredArgsConstructor
public class UserSetMfaSeedPipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<UserSetMfaSeedStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param secondFactorSeed
   * @param original
   * @param last
   * @return
   */
  public User setMfaSeed(final String secondFactorSeed, final User original,
      final Function<UserChangeProposal, User> last) {
    Function<UserSetMfaSeedTransition, User> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.apply(t.getData());
    };
    List<UserSetMfaSeedStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<UserSetMfaSeedTransition, User> pipeline = reversed.stream().reduce(finalHandler,
        (next, step) -> transition -> step.setMfaSeed(transition, next), (f1, f2) -> f1);
    return pipeline.apply(UserSetMfaSeedTransition.builder().data(new UserChangeProposal(original))
        .original(original).build());
  }
}
