package net.civeira.phylax.features.access.user.transition.unlock;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.user.User;
import net.civeira.phylax.features.access.user.transition.UserChangeProposal;

@ApplicationScoped
@RequiredArgsConstructor
public class UserUnlockPipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<UserUnlockStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public User unlock(final User original, final Function<UserChangeProposal, User> last) {
    Function<UserUnlockTransition, User> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.apply(t.getData());
    };
    List<UserUnlockStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<UserUnlockTransition, User> pipeline = reversed.stream().reduce(finalHandler,
        (next, step) -> transition -> step.unlock(transition, next), (f1, f2) -> f1);
    return pipeline.apply(UserUnlockTransition.builder().data(new UserChangeProposal(original))
        .original(original).build());
  }
}
