package net.civeira.phylax.features.access.useracceptedtermnsofuse.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.useracceptedtermnsofuse.domain.UserAcceptedTermnsOfUse;
import net.civeira.phylax.features.access.useracceptedtermnsofuse.domain.event.UserAcceptedTermnsOfUseEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class UserAcceptedTermnsOfUseEventDispacher {

  /**
   * @autogenerated EventDispacherGenerator
   */
  private final Event<UserAcceptedTermnsOfUseEvent> emitter;

  /**
   * @autogenerated EventDispacherGenerator
   * @param entity
   */
  public void dispatch(final UserAcceptedTermnsOfUse entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
