package net.civeira.phylax.features.access.useracceptedtermnsofuse.transition.delete;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;
import java.util.function.Supplier;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.useracceptedtermnsofuse.UserAcceptedTermnsOfUse;

@ApplicationScoped
@RequiredArgsConstructor
public class UserAcceptedTermnsOfUseDeletePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<UserAcceptedTermnsOfUseDeleteStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public UserAcceptedTermnsOfUse delete(final UserAcceptedTermnsOfUse original,
      final Supplier<UserAcceptedTermnsOfUse> last) {
    Function<UserAcceptedTermnsOfUseDeleteTransition, UserAcceptedTermnsOfUse> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.get();
    };
    List<UserAcceptedTermnsOfUseDeleteStep> reversed =
        new ArrayList<>(steps.stream().toList()).reversed();
    Function<UserAcceptedTermnsOfUseDeleteTransition, UserAcceptedTermnsOfUse> pipeline =
        reversed.stream().reduce(finalHandler,
            (next, step) -> transition -> step.delete(transition, next), (f1, f2) -> f1);
    return pipeline
        .apply(UserAcceptedTermnsOfUseDeleteTransition.builder().original(original).build());
  }
}
