package net.civeira.phylax.features.access.useraccesstemporalcode.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.useraccesstemporalcode.UserAccessTemporalCode;
import net.civeira.phylax.features.access.useraccesstemporalcode.event.UserAccessTemporalCodeEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class UserAccessTemporalCodeEventDispacher {

  /**
   * @autogenerated EventDispacherGenerator
   */
  private final Event<UserAccessTemporalCodeEvent> emitter;

  /**
   * @autogenerated EventDispacherGenerator
   * @param entity
   */
  public void dispatch(final UserAccessTemporalCode entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
