package net.civeira.phylax.features.access.useridentity.infrastructure.event;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.event.Event;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.useridentity.domain.UserIdentity;
import net.civeira.phylax.features.access.useridentity.domain.event.UserIdentityEvent;

@ApplicationScoped
@RequiredArgsConstructor
public class UserIdentityEventDispatcher {

  /**
   * @autogenerated EventDispatcherGenerator
   */
  private final Event<UserIdentityEvent> emitter;

  /**
   * @autogenerated EventDispatcherGenerator
   * @param entity
   */
  public void dispatch(final UserIdentity entity) {
    entity.getRecordedEvents().forEach(emitter::fire);
  }
}
