package net.civeira.phylax.features.access.useridentity.transition.delete;

import java.util.ArrayList;
import java.util.List;
import java.util.function.Function;
import java.util.function.Supplier;

import jakarta.enterprise.context.ApplicationScoped;
import jakarta.enterprise.inject.Instance;
import lombok.RequiredArgsConstructor;
import net.civeira.phylax.features.access.useridentity.UserIdentity;

@ApplicationScoped
@RequiredArgsConstructor
public class UserIdentityDeletePipeline {

  /**
   * @autogenerated UpdateEntityRuleGenerator
   */
  private final Instance<UserIdentityDeleteStep> steps;

  /**
   * @autogenerated UpdateEntityRuleGenerator
   * @param original
   * @param last
   * @return
   */
  public UserIdentity delete(final UserIdentity original, final Supplier<UserIdentity> last) {
    Function<UserIdentityDeleteTransition, UserIdentity> finalHandler = t -> {
      if (t.hasErrors()) {
        throw t.asConstraintException();
      }
      return last.get();
    };
    List<UserIdentityDeleteStep> reversed = new ArrayList<>(steps.stream().toList()).reversed();
    Function<UserIdentityDeleteTransition, UserIdentity> pipeline = reversed.stream().reduce(
        finalHandler, (next, step) -> transition -> step.delete(transition, next), (f1, f2) -> f1);
    return pipeline.apply(UserIdentityDeleteTransition.builder().original(original).build());
  }
}
