/* @autogenerated */
package net.civeira.phylax.common.exception;

import static org.junit.jupiter.api.Assertions.*;

import org.junit.jupiter.api.Test;

class NotAllowedExceptionUnitTest {

  @Test
  void testConstructorStoresMessage() {
    String expectedMessage = "User is not authorized to perform this action.";
    NotAllowedException ex = new NotAllowedException(expectedMessage);

    assertEquals(expectedMessage, ex.getMessage());
  }

  @Test
  void testExceptionIsRuntimeException() {
    NotAllowedException ex = new NotAllowedException("Access denied");

    assertTrue(ex instanceof RuntimeException);
  }

  @Test
  void testThrowAndCatchNotAllowedException() {
    String message = "Restricted area";

    Exception exception = assertThrows(NotAllowedException.class, () -> {
      throw new NotAllowedException(message);
    });

    assertEquals(message, exception.getMessage());
  }
}
