/* @autogenerated */
package net.civeira.phylax.common.infrastructure.sql;

import static org.junit.jupiter.api.Assertions.*;

import java.sql.SQLException;

import org.junit.jupiter.api.Test;

class NotEmptyChildsExceptionUnitTest {

  @Test
  void testConstructorWithOnlySQLException() {
    SQLException sqlEx = new SQLException("Foreign key violation");
    NotEmptyChildsException ex = new NotEmptyChildsException(sqlEx);

    assertEquals(sqlEx, ex.getCause());
    assertTrue(ex instanceof UncheckedSqlException);
  }

  @Test
  void testConstructorWithMessageAndSQLException() {
    SQLException sqlEx = new SQLException("Cannot delete parent row");
    String message = "Child records exist";
    NotEmptyChildsException ex = new NotEmptyChildsException(message, sqlEx);

    assertEquals(sqlEx, ex.getCause());
    assertEquals(message, ex.getMessage());
    assertTrue(ex instanceof UncheckedSqlException);
  }
}
