/* @autogenerated */
package net.civeira.phylax.common.infrastructure.sql;

import static org.junit.jupiter.api.Assertions.*;

import java.sql.SQLException;

import org.junit.jupiter.api.Test;

class NotExistentReferenceExceptionUnitTest {

  @Test
  void testConstructorWithOnlySQLException() {
    SQLException sqlEx = new SQLException("Parent record not found");
    NotExistentReferenceException ex = new NotExistentReferenceException(sqlEx);

    assertEquals(sqlEx, ex.getCause());
    assertTrue(ex instanceof UncheckedSqlException);
  }

  @Test
  void testConstructorWithMessageAndSQLException() {
    SQLException sqlEx = new SQLException("No parent row");
    String message = "Referential integrity violation";
    NotExistentReferenceException ex = new NotExistentReferenceException(message, sqlEx);

    assertEquals(sqlEx, ex.getCause());
    assertEquals(message, ex.getMessage());
    assertTrue(ex instanceof UncheckedSqlException);
  }
}
