package net.civeira.phylax.common.infrastructure.sql;

import static org.junit.jupiter.api.Assertions.*;

import org.junit.jupiter.api.Test;

class OptimistLockExceptionUnitTest {

  @Test
  void testDefaultConstructor() {
    OptimistLockException ex = new OptimistLockException();

    assertNull(ex.getMessage()); // No message provided
    assertNull(ex.getCause()); // No cause provided
    assertTrue(ex instanceof RuntimeException);
  }

  @Test
  void testConstructorWithMessage() {
    String message = "Optimistic lock failed";
    OptimistLockException ex = new OptimistLockException(message);

    assertEquals(message, ex.getMessage());
    assertNull(ex.getCause()); // No cause provided
    assertTrue(ex instanceof RuntimeException);
  }
}
