package net.civeira.phylax.common.infrastructure.store;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

class RepositoryLinkUnitTest {

  @Test
  void testGetter() {
    RepositoryLink res = new RepositoryLink(null, null);
    Assertions.assertFalse(res.getPublicUrl().isPresent(),
        "The url is not preset with null values");
    RepositoryLink pub = new RepositoryLink(null, "pub");
    Assertions.assertEquals("pub", pub.getPublicUrl().get(), "The url must be stored by the link");
  }
}
