package net.civeira.phylax.common.value.validation;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

class ConstraintFailUnitTest {

  @Test
  @DisplayName("Test the rigth build of the exception")
  void test_constructor() {
    ConstraintFail cf1 = new ConstraintFail("code", "field", "wrong");
    Assertions.assertEquals("code", cf1.getCode(), "The fail must keep code value");
    Assertions.assertEquals("field", cf1.getWrongValues().get(0).getField(),
        "The fail must keep field value");
    Assertions.assertEquals("wrong", cf1.getWrongValues().get(0).getWrongValue(),
        "The fail must keep wrong value");

    ConstraintFail cf2 = new ConstraintFail("code2", "field2", "wrong2", "noop");
    Assertions.assertEquals("code2", cf2.getCode(), "The fail must keep code value");
    Assertions.assertEquals("field2", cf2.getWrongValues().get(0).getField(),
        "The fail must keep field value");
    Assertions.assertEquals("wrong2", cf2.getWrongValues().get(0).getWrongValue(),
        "The fail must keep wrong value");
    Assertions.assertEquals("noop", cf2.getWrongValues().get(0).getErrorMessage(),
        "The fail must keep error message value");


  }
}
