package net.civeira.phylax.common.value.validation.validators;

import static org.junit.jupiter.api.Assertions.*;

import org.junit.jupiter.api.Test;

class CreditCardValidatorUnitTest {

  @Test
  void shouldValidateValidCard() {
    CreditCardValidator validator = new CreditCardValidator("Error");
    assertTrue(validator.validate("4111111111111111").isValid()); // Visa test number
  }

  @Test
  void shouldInvalidateInvalidCard() {
    CreditCardValidator validator = new CreditCardValidator("Error");
    assertFalse(validator.validate("1234567812345678").isValid());
  }

  @Test
  void shouldInvalidateNonNumericCard() {
    CreditCardValidator validator = new CreditCardValidator("Error");
    assertFalse(validator.validate("4111abcd11111111").isValid());
  }

  @Test
  void shouldInvalidateNullCard() {
    CreditCardValidator validator = new CreditCardValidator("Error");
    assertFalse(validator.validate(null).isValid());
  }

  @Test
  void shouldInvalidateEmptyCard() {
    CreditCardValidator validator = new CreditCardValidator("Error");
    assertFalse(validator.validate("").isValid());
  }

  @Test
  void shouldInvalidateCardWithSpaces() {
    CreditCardValidator validator = new CreditCardValidator("Error");
    assertFalse(validator.validate("4111 1111 1111 1111").isValid());
  }

  @Test
  void shouldInvalidateShortCardNumber() {
    CreditCardValidator validator = new CreditCardValidator("Error");
    assertFalse(validator.validate("411111").isValid());
  }

  @Test
  void shouldInvalidateLongCardNumber() {
    CreditCardValidator validator = new CreditCardValidator("Error");
    assertFalse(validator.validate("41111111111111111111").isValid());
  }

  @Test
  void shouldValidateAnotherValidLuhnCard() {
    CreditCardValidator validator = new CreditCardValidator("Error");
    assertTrue(validator.validate("5500000000000004").isValid()); // Mastercard test number
  }
}
