package net.civeira.phylax.features.access.apikeyclient.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class ScopesVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property scopes of api key client ")
  void test_scopes_v_o_builder() {
    Assertions.assertFalse(ScopesVO.nullValue().getScopes().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(ScopesVO.tryFrom(null).getScopes().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals("one", ScopesVO.from("one").getScopes().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> ScopesVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", ScopesVO.from("one").toString());
  }
}
