package net.civeira.phylax.features.access.loginprovider.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class CertificateVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property certificate of login provider ")
  void test_certificate_v_o_builder() {
    Assertions.assertFalse(CertificateVO.nullValue().getCertificate().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(CertificateVO.tryFrom(null).getCertificate().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals("one", CertificateVO.from("one").getCertificate().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> CertificateVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", CertificateVO.from("one").toString());
  }
}
