package net.civeira.phylax.features.access.loginprovider.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class DisabledVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property disabled of login provider ")
  void test_disabled_v_o_builder() {
    Assertions.assertFalse(DisabledVO.nullValue().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(DisabledVO.tryFrom(null).getValue().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals(true, DisabledVO.from(true).getValue().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> DisabledVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + true + "]", DisabledVO.from(true).toString());
  }
}
