package net.civeira.phylax.features.access.loginprovider.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class PrivateKeyVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property private key of login provider ")
  void test_private_key_v_o_builder() {
    Assertions.assertFalse(PrivateKeyVO.nullValue().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(PrivateKeyVO.tryFrom(null).getValue().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals("one", PrivateKeyVO.from("one").getValue().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> PrivateKeyVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", PrivateKeyVO.from("one").toString());
  }
}
