package net.civeira.phylax.features.access.loginprovider.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class PublicKeyVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property public key of login provider ")
  void test_public_key_v_o_builder() {
    Assertions.assertFalse(PublicKeyVO.nullValue().getPublicKey().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(PublicKeyVO.tryFrom(null).getPublicKey().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals("one", PublicKeyVO.from("one").getPublicKey().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> PublicKeyVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", PublicKeyVO.from("one").toString());
  }
}
