package net.civeira.phylax.features.access.loginprovider.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class DirectAccessVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property direct access of login provider ")
  void test_direct_access_v_o_builder() {
    Assertions.assertFalse(DirectAccessVO.nullValue().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(DirectAccessVO.tryFrom(null).getValue().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals(true, DirectAccessVO.from(true).getValue().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> DirectAccessVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + true + "]", DirectAccessVO.from(true).toString());
  }
}
