package net.civeira.phylax.features.access.loginprovider.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class NameVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property name of login provider ")
  void test_name_v_o_builder() {
    Assertions.assertThrows(ConstraintException.class, () -> NameVO.tryFrom(null));
    Assertions.assertEquals("one", NameVO.from("one").getValue());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> NameVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", NameVO.from("one").toString());
  }
}
