package net.civeira.phylax.features.access.relyingparty.domain;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

class RelyingPartyChangeSetUnitTest {

  /**
   * @autogenerated CachedGenerator
   */
  @Test
  @DisplayName("Test a entity reference contruction")
  void test_relying_party_change_set_builder() {
    RelyingPartyChangeSet one = RelyingPartyChangeSet.builder().uid("one").code("one").apiKey("one")
        .enabled(true).version(1).newUid().build();
    one.setUid("two");
    Assertions.assertEquals("two", one.getUid().get().getUid(),
        "Value must equals to the assigned value");
    one.unset("uid");
    Assertions.assertFalse(one.getUid().isPresent(), "After unset, the valeue must be dissapear");
    Assertions.assertEquals("one", one.getCode().get().getCode(),
        "Value must equals to the intial value");
    one.setCode("two");
    Assertions.assertEquals("two", one.getCode().get().getCode(),
        "Value must equals to the assigned value");
    one.unset("code");
    Assertions.assertFalse(one.getCode().isPresent(), "After unset, the valeue must be dissapear");
    Assertions.assertEquals("one", one.getApiKey().get().getApiKey(),
        "Value must equals to the intial value");
    one.setApiKey("two");
    Assertions.assertEquals("two", one.getApiKey().get().getApiKey(),
        "Value must equals to the assigned value");
    one.unset("apiKey");
    Assertions.assertFalse(one.getApiKey().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals(true, one.getEnabled().get().isEnabled(),
        "Value must equals to the intial value");
    one.setEnabled(false);
    Assertions.assertEquals(false, one.getEnabled().get().isEnabled(),
        "Value must equals to the assigned value");
    one.unset("enabled");
    Assertions.assertFalse(one.getEnabled().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals(0, one.getVersion().get().getVersion().get(),
        "Value must equals to the intial value");
    one.setVersion(2);
    Assertions.assertEquals(2, one.getVersion().get().getVersion().get(),
        "Value must equals to the assigned value");
    one.unset("version");
    Assertions.assertFalse(one.getVersion().isPresent(),
        "After unset, the valeue must be dissapear");
  }
}
