package net.civeira.phylax.features.access.relyingparty.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class ApiKeyVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property api key of relying party ")
  void test_api_key_v_o_builder() {
    Assertions.assertThrows(ConstraintException.class, () -> ApiKeyVO.tryFrom(null));
    Assertions.assertEquals("one", ApiKeyVO.from("one").getApiKey());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> ApiKeyVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", ApiKeyVO.from("one").toString());
  }
}
