package net.civeira.phylax.features.access.role.domain.valueobject;

import java.util.List;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;
import net.civeira.phylax.features.access.role.domain.Domains;
import net.civeira.phylax.features.access.securitydomain.domain.SecurityDomainReference;

class DomainsVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property domains of role ")
  void test_domains_v_o_builder() {
    Assertions.assertTrue(DomainsVO.tryFrom(null).getDomains().isEmpty());
    List<Domains> list = List.of();
    Assertions.assertEquals(list, DomainsVO.from(list).getDomains());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> DomainsVO.tryFrom(falingInstance));
    Assertions
        .assertEquals(
            "[" + List.of(Domains.builder().uid("one")
                .securityDomain(SecurityDomainReference.of("one")).version(1).build()) + "]",
            DomainsVO
                .from(List.of(Domains.builder().uid("one")
                    .securityDomain(SecurityDomainReference.of("one")).version(1).build()))
                .toString());
  }
}
