package net.civeira.phylax.features.access.role.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;
import net.civeira.phylax.common.value.validation.ConstraintFailList;
import net.civeira.phylax.features.access.tenant.domain.TenantReference;

class TenantVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property tenant of role ")
  void test_tenant_v_o_builder() {
    ConstraintFailList fails = new ConstraintFailList();
    Assertions.assertEquals("10", TenantVO.fromReference("10").getTenantUid().orElse(null),
        "The builder must store the reference value");
    Assertions.assertEquals("11", TenantVO.tryFromReference("11").getTenantUid().orElse(null),
        "The builder must store the reference value");
    Assertions.assertEquals("12",
        TenantVO.tryFromReference("12", fails).getTenantUid().orElse(null),
        "The builder must store the reference value");
    Assertions.assertTrue(fails.isEmpty());
    Assertions.assertFalse(TenantVO.nullValue().getTenant().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(TenantVO.tryFrom(null).getTenant().isPresent(),
        "With a null value, no present result");
    Assertions.assertFalse(TenantVO.fromReference(null).getTenant().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals(TenantReference.of("one"),
        TenantVO.from(TenantReference.of("one")).getTenant().orElse(null));
    Assertions.assertThrows(ConstraintException.class, () -> TenantVO.tryFrom(new Object() {}));
    Assertions.assertEquals("[" + TenantReference.of("one") + "]",
        TenantVO.from(TenantReference.of("one")).toString());
  }
}
