package net.civeira.phylax.features.access.securitydomain.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class LevelVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property level of security domain ")
  void test_level_v_o_builder() {
    Assertions.assertThrows(ConstraintException.class, () -> LevelVO.tryFrom(null));
    Assertions.assertEquals(1, LevelVO.from(1).getValue());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> LevelVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + 1 + "]", LevelVO.from(1).toString());
  }
}
