package net.civeira.phylax.features.access.securitydomain.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class ManageAllVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property manage all of security domain ")
  void test_manage_all_v_o_builder() {
    Assertions.assertFalse(ManageAllVO.nullValue().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(ManageAllVO.tryFrom(null).getValue().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals(true, ManageAllVO.from(true).getValue().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> ManageAllVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + true + "]", ManageAllVO.from(true).toString());
  }
}
