package net.civeira.phylax.features.access.securitydomain.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class ReadAllVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property read all of security domain ")
  void test_read_all_v_o_builder() {
    Assertions.assertFalse(ReadAllVO.nullValue().isReadAll(),
        "A empty vo should have a null value");
    Assertions.assertFalse(ReadAllVO.tryFrom(null).isReadAll(),
        "With a null value, no present result");
    Assertions.assertEquals(true, ReadAllVO.from(true).isReadAll());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> ReadAllVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + true + "]", ReadAllVO.from(true).toString());
  }
}
