package net.civeira.phylax.features.access.securitydomain.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class WriteAllVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property write all of security domain ")
  void test_write_all_v_o_builder() {
    Assertions.assertFalse(WriteAllVO.nullValue().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(WriteAllVO.tryFrom(null).getValue().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals(true, WriteAllVO.from(true).getValue().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> WriteAllVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + true + "]", WriteAllVO.from(true).toString());
  }
}
