package net.civeira.phylax.features.access.securityscope.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class ResourceVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property resource of security scope ")
  void test_resource_v_o_builder() {
    Assertions.assertThrows(ConstraintException.class, () -> ResourceVO.tryFrom(null));
    Assertions.assertEquals("one", ResourceVO.from("one").getResource());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> ResourceVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", ResourceVO.from("one").toString());
  }
}
