package net.civeira.phylax.features.access.securityscope.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class ScopeVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property scope of security scope ")
  void test_scope_v_o_builder() {
    Assertions.assertThrows(ConstraintException.class, () -> ScopeVO.tryFrom(null));
    Assertions.assertEquals("one", ScopeVO.from("one").getScope());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> ScopeVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", ScopeVO.from("one").toString());
  }
}
