package net.civeira.phylax.features.access.securityscope.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;
import net.civeira.phylax.features.access.securityscope.domain.SecurityScopeVisibilityOptions;

class VisibilityVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property visibility of security scope ")
  void test_visibility_v_o_builder() {
    Assertions.assertThrows(ConstraintException.class, () -> VisibilityVO.tryFrom("_PUBLIC_"));
    Assertions.assertEquals(SecurityScopeVisibilityOptions.PUBLIC,
        VisibilityVO.tryFrom("PUBLIC").getVisibility().orElse(null));
    Assertions.assertFalse(VisibilityVO.nullValue().getVisibility().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(VisibilityVO.tryFrom(null).getVisibility().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals(SecurityScopeVisibilityOptions.PUBLIC,
        VisibilityVO.from(SecurityScopeVisibilityOptions.PUBLIC).getVisibility().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> VisibilityVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + SecurityScopeVisibilityOptions.PUBLIC + "]",
        VisibilityVO.from(SecurityScopeVisibilityOptions.PUBLIC).toString());
  }
}
