package net.civeira.phylax.features.access.securityscope.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class UidVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property uid of security scope ")
  void test_uid_v_o_builder() {
    Assertions.assertThrows(ConstraintException.class, () -> UidVO.tryFrom(null));
    Assertions.assertEquals("one", UidVO.from("one").getValue());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> UidVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", UidVO.from("one").toString());
  }
}
