package net.civeira.phylax.features.access.tenant.domain;

import java.time.LocalDateTime;
import java.time.ZoneId;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

class TenantChangeSetUnitTest {

  /**
   * @autogenerated CachedGenerator
   */
  @Test
  @DisplayName("Test a entity reference contruction")
  void test_tenant_change_set_builder() {
    TenantChangeSet one = new TenantChangeSet().uid("one").name("one").root(true).domain("one")
        .enabled(true).markForDelete(true).markForDeleteTime(LocalDateTime.of(1980, 8, 20, 0, 0)
            .atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime())
        .version(1).newUid();
    one.uid("two");
    Assertions.assertEquals("two", one.getUid().get(), "Value must equals to the assigned value");
    one.unset("uid");
    Assertions.assertFalse(one.getUid().isPresent(), "After unset, the valeue must be dissapear");
    Assertions.assertEquals("one", one.getName().get(), "Value must equals to the intial value");
    one.name("two");
    Assertions.assertEquals("two", one.getName().get(), "Value must equals to the assigned value");
    one.unset("name");
    Assertions.assertFalse(one.getName().isPresent(), "After unset, the valeue must be dissapear");
    Assertions.assertEquals(true, one.getRoot().get(), "Value must equals to the intial value");
    one.root(false);
    Assertions.assertEquals(false, one.getRoot().get(), "Value must equals to the assigned value");
    one.unset("root");
    Assertions.assertFalse(one.getRoot().isPresent(), "After unset, the valeue must be dissapear");
    Assertions.assertEquals("one", one.getDomain().get(), "Value must equals to the intial value");
    one.domain("two");
    Assertions.assertEquals("two", one.getDomain().get(),
        "Value must equals to the assigned value");
    one.unset("domain");
    Assertions.assertFalse(one.getDomain().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals(true, one.getEnabled().get(), "Value must equals to the intial value");
    one.enabled(false);
    Assertions.assertEquals(false, one.getEnabled().get(),
        "Value must equals to the assigned value");
    one.unset("enabled");
    Assertions.assertFalse(one.getEnabled().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals(true, one.getMarkForDelete().get(),
        "Value must equals to the intial value");
    one.markForDelete(false);
    Assertions.assertEquals(false, one.getMarkForDelete().get(),
        "Value must equals to the assigned value");
    one.unset("markForDelete");
    Assertions.assertFalse(one.getMarkForDelete().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals(
        LocalDateTime.of(1980, 8, 20, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime(),
        one.getMarkForDeleteTime().get(), "Value must equals to the intial value");
    one.markForDeleteTime(
        LocalDateTime.of(1981, 9, 6, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime());
    Assertions.assertEquals(
        LocalDateTime.of(1981, 9, 6, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime(),
        one.getMarkForDeleteTime().get(), "Value must equals to the assigned value");
    one.unset("markForDeleteTime");
    Assertions.assertFalse(one.getMarkForDeleteTime().isPresent(),
        "After unset, the valeue must be dissapear");
    Assertions.assertEquals(0, one.getVersion().get(), "Value must equals to the intial value");
    one.version(2);
    Assertions.assertEquals(2, one.getVersion().get(), "Value must equals to the assigned value");
    one.unset("version");
    Assertions.assertFalse(one.getVersion().isPresent(),
        "After unset, the valeue must be dissapear");
  }
}
