package net.civeira.phylax.features.access.tenant.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class DomainVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property domain of tenant ")
  void test_domain_v_o_builder() {
    Assertions.assertThrows(ConstraintException.class, () -> DomainVO.tryFrom(null));
    Assertions.assertEquals("one", DomainVO.from("one").getValue());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> DomainVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", DomainVO.from("one").toString());
  }
}
