package net.civeira.phylax.features.access.tenant.domain.valueobject;

import java.time.LocalDateTime;
import java.time.ZoneId;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class MarkForDeleteTimeVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property mark for delete time of tenant ")
  void test_mark_for_delete_time_v_o_builder() {
    Assertions.assertFalse(MarkForDeleteTimeVO.nullValue().getMarkForDeleteTime().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(MarkForDeleteTimeVO.tryFrom(null).getMarkForDeleteTime().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals(
        LocalDateTime.of(1980, 8, 20, 0, 0).atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime(),
        MarkForDeleteTimeVO.from(LocalDateTime.of(1980, 8, 20, 0, 0)
            .atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime()).getMarkForDeleteTime()
            .orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> MarkForDeleteTimeVO.tryFrom(falingInstance));
    Assertions.assertEquals(
        "[" + LocalDateTime.of(1980, 8, 20, 0, 0).atZone(ZoneId.of("Europe/Madrid"))
            .toOffsetDateTime() + "]",
        MarkForDeleteTimeVO.from(LocalDateTime.of(1980, 8, 20, 0, 0)
            .atZone(ZoneId.of("Europe/Madrid")).toOffsetDateTime()).toString());
  }
}
