package net.civeira.phylax.features.access.tenant.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class MarkForDeleteVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property mark for delete of tenant ")
  void test_mark_for_delete_v_o_builder() {
    Assertions.assertThrows(ConstraintException.class, () -> MarkForDeleteVO.tryFrom(null));
    Assertions.assertEquals(true, MarkForDeleteVO.from(true).isMarkForDelete());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> MarkForDeleteVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + true + "]", MarkForDeleteVO.from(true).toString());
  }
}
