package net.civeira.phylax.features.access.tenant.domain.valueobject;

import java.util.List;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;
import net.civeira.phylax.features.access.relyingparty.domain.RelyingPartyReference;
import net.civeira.phylax.features.access.tenant.domain.RelingParties;

class RelingPartiesVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property reling parties of tenant ")
  void test_reling_parties_v_o_builder() {
    Assertions.assertTrue(RelingPartiesVO.tryFrom(null).getValue().isEmpty());
    List<RelingParties> list = List.of();
    Assertions.assertEquals(list, RelingPartiesVO.from(list).getValue());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> RelingPartiesVO.tryFrom(falingInstance));
    Assertions.assertEquals(
        "[" + List.of(RelingParties.builder().uid("one")
            .relyingParty(RelyingPartyReference.of("one")).version(1).build()) + "]",
        RelingPartiesVO.from(List.of(RelingParties.builder().uid("one")
            .relyingParty(RelyingPartyReference.of("one")).version(1).build())).toString());
  }
}
