package net.civeira.phylax.features.access.tenant.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class RootVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property root of tenant ")
  void test_root_v_o_builder() {
    Assertions.assertFalse(RootVO.nullValue().isRoot(), "A empty vo should have a null value");
    Assertions.assertFalse(RootVO.tryFrom(null).isRoot(), "With a null value, no present result");
    Assertions.assertEquals(true, RootVO.from(true).isRoot());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> RootVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + true + "]", RootVO.from(true).toString());
  }
}
