package net.civeira.phylax.features.access.tenant.domain.valueobject;

import java.util.List;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;
import net.civeira.phylax.features.access.tenant.domain.TrustedClients;
import net.civeira.phylax.features.access.trustedclient.domain.TrustedClientReference;

class TrustedClientsVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property trusted clients of tenant ")
  void test_trusted_clients_v_o_builder() {
    Assertions.assertTrue(TrustedClientsVO.tryFrom(null).getValue().isEmpty());
    List<TrustedClients> list = List.of();
    Assertions.assertEquals(list, TrustedClientsVO.from(list).getValue());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> TrustedClientsVO.tryFrom(falingInstance));
    Assertions
        .assertEquals(
            "[" + List.of(TrustedClients.builder().uid("one")
                .trustedClient(TrustedClientReference.of("one")).version(1).build()) + "]",
            TrustedClientsVO
                .from(List.of(TrustedClients.builder().uid("one")
                    .trustedClient(TrustedClientReference.of("one")).version(1).build()))
                .toString());
  }
}
