package net.civeira.phylax.features.access.tenant.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class AccessToAllApplicationsVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property access to all applications of tenant ")
  void test_access_to_all_applications_v_o_builder() {
    Assertions.assertFalse(AccessToAllApplicationsVO.nullValue().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(AccessToAllApplicationsVO.tryFrom(null).getValue().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals(true, AccessToAllApplicationsVO.from(true).getValue().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> AccessToAllApplicationsVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + true + "]", AccessToAllApplicationsVO.from(true).toString());
  }
}
