package net.civeira.phylax.features.access.tenant.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;
import net.civeira.phylax.common.value.validation.ConstraintFailList;
import net.civeira.phylax.features.access.relyingparty.RelyingPartyReference;

class RelyingPartyVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property relying party of tenant relying party ")
  void test_relying_party_v_o_builder() {
    ConstraintFailList fails = new ConstraintFailList();
    Assertions.assertEquals("10", RelyingPartyVO.fromReference("10").getReferenceValue(),
        "The builder must store the reference value");
    Assertions.assertEquals("11", RelyingPartyVO.tryFromReference("11").getReferenceValue(),
        "The builder must store the reference value");
    Assertions.assertEquals("12", RelyingPartyVO.tryFromReference("12", fails).getReferenceValue(),
        "The builder must store the reference value");
    Assertions.assertTrue(fails.isEmpty());
    Assertions.assertThrows(ConstraintException.class, () -> RelyingPartyVO.tryFrom(null));
    Assertions.assertEquals(RelyingPartyReference.of("one"),
        RelyingPartyVO.from(RelyingPartyReference.of("one")).getValue());
    Assertions.assertThrows(ConstraintException.class,
        () -> RelyingPartyVO.tryFrom(new Object() {}));
    Assertions.assertEquals("[" + RelyingPartyReference.of("one") + "]",
        RelyingPartyVO.from(RelyingPartyReference.of("one")).toString());
  }
}
