package net.civeira.phylax.features.access.tenant.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class VersionVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property version of tenant ")
  void test_version_v_o_builder() {
    Assertions.assertFalse(VersionVO.nullValue().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(VersionVO.tryFrom(null).getValue().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals(1, VersionVO.from(1).getValue().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class, () -> VersionVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + 1 + "]", VersionVO.from(1).toString());
  }
}
