package net.civeira.phylax.features.access.tenantconfig.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class AllowRecoverPassVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property allow recover pass of tenant config ")
  void test_allow_recover_pass_v_o_builder() {
    Assertions.assertFalse(AllowRecoverPassVO.nullValue().isAllowRecoverPass(),
        "A empty vo should have a null value");
    Assertions.assertFalse(AllowRecoverPassVO.tryFrom(null).isAllowRecoverPass(),
        "With a null value, no present result");
    Assertions.assertEquals(true, AllowRecoverPassVO.from(true).isAllowRecoverPass());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> AllowRecoverPassVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + true + "]", AllowRecoverPassVO.from(true).toString());
  }
}
