package net.civeira.phylax.features.access.tenantconfig.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class AllowRegisterVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property allow register of tenant config ")
  void test_allow_register_v_o_builder() {
    Assertions.assertFalse(AllowRegisterVO.nullValue().isAllowRegister(),
        "A empty vo should have a null value");
    Assertions.assertFalse(AllowRegisterVO.tryFrom(null).isAllowRegister(),
        "With a null value, no present result");
    Assertions.assertEquals(true, AllowRegisterVO.from(true).isAllowRegister());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> AllowRegisterVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + true + "]", AllowRegisterVO.from(true).toString());
  }
}
