package net.civeira.phylax.features.access.tenantconfig.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class DisabledUserEmailVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property disabled user email of tenant config ")
  void test_disabled_user_email_v_o_builder() {
    Assertions.assertFalse(DisabledUserEmailVO.nullValue().getDisabledUserEmail().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(DisabledUserEmailVO.tryFrom(null).getDisabledUserEmail().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals("one",
        DisabledUserEmailVO.from("one").getDisabledUserEmail().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> DisabledUserEmailVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", DisabledUserEmailVO.from("one").toString());
  }
}
