package net.civeira.phylax.features.access.tenantconfig.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class EnableRegisterUsersVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property enable register users of tenant config ")
  void test_enable_register_users_v_o_builder() {
    Assertions.assertFalse(EnableRegisterUsersVO.nullValue().isEnableRegisterUsers(),
        "A empty vo should have a null value");
    Assertions.assertFalse(EnableRegisterUsersVO.tryFrom(null).isEnableRegisterUsers(),
        "With a null value, no present result");
    Assertions.assertEquals(true, EnableRegisterUsersVO.from(true).isEnableRegisterUsers());
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> EnableRegisterUsersVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + true + "]", EnableRegisterUsersVO.from(true).toString());
  }
}
