package net.civeira.phylax.features.access.tenantconfig.domain.valueobject;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import net.civeira.phylax.common.exception.ConstraintException;

class EnabledUserEmailVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property enabled user email of tenant config ")
  void test_enabled_user_email_v_o_builder() {
    Assertions.assertFalse(EnabledUserEmailVO.nullValue().getEnabledUserEmail().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(EnabledUserEmailVO.tryFrom(null).getEnabledUserEmail().isPresent(),
        "With a null value, no present result");
    Assertions.assertEquals("one",
        EnabledUserEmailVO.from("one").getEnabledUserEmail().orElse(null));
    Object falingInstance = new Object() {};
    Assertions.assertThrows(ConstraintException.class,
        () -> EnabledUserEmailVO.tryFrom(falingInstance));
    Assertions.assertEquals("[" + "one" + "]", EnabledUserEmailVO.from("one").toString());
  }
}
